	private function utf8_to_unicode($str, $strict = false) {
		$mState = 0;
		$mUcs4 = 0;
		$mBytes = 1;
		$out = array();
		$len = strlen($str);
		for ($i = 0; $i < $len; $i++) {
			$in = ord($str {$i});
			if ($mState == 0) {
				if (0 == (0x80 & ($in))) {
					$out[] = $in;
					$mBytes = 1;
				} elseif (0xC0 == (0xE0 & ($in))) {
						$mUcs4 = ($in);
						$mUcs4 = ($mUcs4 & 0x1F) << 6;
						$mState = 1;
						$mBytes = 2;
				} elseif (0xE0 == (0xF0 & ($in))) {
						$mUcs4 = ($in);
						$mUcs4 = ($mUcs4 & 0x0F) << 12;
						$mState = 2;
						$mBytes = 3;
				} elseif (0xF0 == (0xF8 & ($in))) {
						$mUcs4 = ($in);
						$mUcs4 = ($mUcs4 & 0x07) << 18;
						$mState = 3;
						$mBytes = 4;
				} elseif (0xF8 == (0xFC & ($in))) {
						$mUcs4 = ($in);
						$mUcs4 = ($mUcs4 & 0x03) << 24;
						$mState = 4;
						$mBytes = 5;
				} elseif (0xFC == (0xFE & ($in))) {
						$mUcs4 = ($in);
						$mUcs4 = ($mUcs4 & 1) << 30;
						$mState = 5;
						$mBytes = 6;
				} elseif ($strict) {
					trigger_error('utf8_to_unicode: Illegal sequence identifier ' . 'in UTF-8 at byte ' . $i, E_USER_WARNING);
					return false;
				}
			} else {
				if (0x80 == (0xC0 & ($in))) {
					$shift = ($mState - 1) * 6;
					$tmp = $in;
					$tmp = ($tmp & 0x0000003F) << $shift;
					$mUcs4 |= $tmp;
					if (0 ==--$mState) {
						if (((2 == $mBytes) && ($mUcs4 < 0x0080)) || ((3 == $mBytes) && ($mUcs4 < 0x0800)) || ((4 == $mBytes) && ($mUcs4 < 0x10000)) || (4 < $mBytes) || (($mUcs4 & 0xFFFFF800) == 0xD800) || ($mUcs4 > 0x10FFFF)) {
							if ($strict) {
								trigger_error('utf8_to_unicode: Illegal sequence or codepoint ' . 'in UTF-8 at byte ' . $i, E_USER_WARNING);
								return false;
							}
						}
						if (0xFEFF != $mUcs4) {
							$out[] = $mUcs4;
						}
						$mState = 0;
						$mUcs4 = 0;
						$mBytes = 1;
					}
				} elseif ($strict) {
					trigger_error('utf8_to_unicode: Incomplete multi-octet ' . '	 sequence in UTF-8 at byte ' . $i, E_USER_WARNING);
					return false;
				}
			}
		}
		return $out;
	}
	public function utf8_to_html($str) {
		$ret = '';
		foreach ($this->utf8_to_unicode($str) as $cp) {
			if ($cp < 0x80)
				$ret .= chr($cp);
			elseif ($cp < 0x100)
				$ret .= "&#$cp;";
			else
				$ret .= '&#x' . dechex($cp) . ';';
		}
		return $ret;
	}
